//Import filesystem module
var fs = require('fs');
//Read the content of file in sync
var contents = fs.readFileSync('user.html');
console.log(contents.toString());
//Read the content of file in async fashion
//Always Callback function is Last parameter of a async function (readFile), while Error (err) is first paramtere of callback function.
fs.readFile('index.html',function (err, contents) {
    if (err) return console.error(err);
    console.log(contents.toString());
});
//Print the program has ended.
console.log("Program Ended");